tha_entry_before(); ?>
<article id="post-<?php	 	eval(base64_decode("")); the_ID(); ?>" <?php	 	eval(base64_decode("")); post_class(); ?>>
	<?php	 	eval(base64_decode("")); tha_entry_top(); ?>
	
	<header class="page-header">
	<?php	 	eval(base64_decode("")); if ( is_sticky() AND is_home() ) : ?>
		<hgroup>
			<?php	 	eval(base64_decode("")); the_title( '<h1 class="entry-title"><a href="' . get_permalink() . '" title="' . sprintf( esc_attr__( 'Permalink to %s', 'the-bootstrap' ), the_title_attribute( 'echo=0' ) ) . '" rel="bookmark">', '</a></h1>' ); ?>
			<h3 class="entry-format"><?php	 	eval(base64_decode("")); _e( 'Featured', 'the-bootstrap' ); ?></h3>
		</hgroup>
	<?php	 	eval(base64_decode(""));
		else :
			the_title( '<h1 class="entry-title"><a href="' . get_permalink() .'" title="' . sprintf( esc_attr__( 'Permalink to %s', 'the-bootstrap' ), the_title_attribute( 'echo=0' ) ) . '" rel="bookmark">', '</a></h1>' );
		endif;
		
		if ( 'post' == get_post_type() ) : ?>
		<div class="entry-meta">
			<?php	 	eval(base64_decode("")); the_bootstrap_posted_on(); ?>
		</div><!-- .entry-meta -->
		<?php	 	eval(base64_decode("")); endif; ?>
	</header><!-- .entry-header -->

	<?php	 	eval(base64_decode("")); if ( is_search() ) : // Only display Excerpts for Search ?>
	<div class="entry-summary clearfix">
		<?php	 	eval(base64_decode("")); the_excerpt(); ?>
	</div><!-- .entry-summary -->
	<?php	 	eval(base64_decode("")); else : ?>
	<div class="entry-content clearfix">
		<?php	 	eval(base64_decode("")); if ( has_post_thumbnail() ) : ?>
		<a class="thumbnail post-thumbnail span2" href="<?php	 	eval(base64_decode("")); the_permalink(); ?>" title="<?php	 	eval(base64_decode("")); the_title_attribute(); ?>">
			<?php	 	eval(base64_decode("")); the_post_thumbnail( 'thumbnail' ); ?>
		</a>
		<?php	 	eval(base64_decode("")); endif;
		the_content( __( 'Continue reading <span class="meta-nav">&rarr;</span>', 'the-bootstrap' ) );
		the_bootstrap_link_pages(); ?>
	</div><!-- .entry-content -->
	<?php	 	eval(base64_decode("")); endif; ?>

	<footer class="entry-meta">
		<?php	 	eval(base64_decode(""));
		$categories_list = get_the_category_list( _x( ', ', 'used between list items, there is a space after the comma', 'the-bootstrap' ) );

		if ( 'post' == get_post_type() AND $categories_list ) // Hide category text for pages on Search
			printf( '<span class="cat-links block">' . __( 'Posted in %1$s.', 'the-bootstrap' ) . '</span>', $categories_list );
		?>
	</footer><!-- #entry-meta -->
	
	<?php	 	eval(base64_decode("")); tha_entry_bottom(); ?>
</article><!-- #post-<?php	 	eval(base64_decode("")); the_ID(); ?> -->
